#include<iostream>

using namespace std;

#define N 1000

int main()
{
	string num = "";

	cout << num.length();

	long long arr[N];

	for (long long i = 0; i < N; i++)
	{
		arr[i] = num[i] - '0';
	}

	long long start = 0;
	long long end = start + 12;
	long long maxProduct = 0;
	long long i = 1;

	while (end <= 1000)
	{
		long long localProduct = 1;
		for (long long i = start; i <= end; i++)
		{
			localProduct *= arr[i];
		}

		cout << "Product " << i++ << ": " << localProduct << endl;

		if (localProduct > maxProduct)
		{
			maxProduct = localProduct;
		}

		start++;
		end++;
	}

	cout << maxProduct << endl;

	return 0;
}